import React, { useEffect } from 'react';

function About() {
  useEffect(() => {
    // 页面加载时的淡入动画
    const aboutSection = document.querySelector('.about');
    aboutSection.style.opacity = '0';
    setTimeout(() => {
      aboutSection.style.transition = 'opacity 1s ease-in';
      aboutSection.style.opacity = '1';
    }, 100);
  }, []);

  return (
    <section className="about">
      <div className="container">
        <div className="about-content">
          <div className="about-text">
            <h2 className="animate-slide-up">MEET Lei Guo</h2>
            <p className="animate-slide-up">
              A real estate broker of Royal LePage Team Realty in Ottawa,
              graduated with a Master of Management and Professional Accounting
              from the Rotman School of Management, University of Toronto. Lei is
              a well-respected Ottawa Agent, TOP 2% of Royal LePage's Realtors in
              Canada!
            </p>
            <button className="btn animate-slide-up">GET CONNECTED</button>
            <div className="badges">
              <img src="/images/badge1.png" alt="Top 2% Award" />
              <img src="/images/badge2.png" alt="Royal LePage Award" />
              <img src="/images/badge3.png" alt="Sales Achievement" />
            </div>
          </div>
          <div className="about-image">
            <img
              src="/images/lei-guo.jpg"
              alt="Lei Guo"
              className="animate-fade-in"
            />
          </div>
        </div>
      </div>
    </section>
  );
}

export default About;
