// src/components/BlogDetail.js
import React from 'react';
import { useParams } from 'react-router-dom';
import './blogdetail.css';

function BlogDetail() {
  const { id } = useParams();

  const blogData = {
    1: {
      title: '2025年1月，渥太华的房产市场迎来了一定的变化',
      content: [
        {
          subtitle: '成交量：买家的议价能力',
          text: '根据渥太华房地产协会（OREB）的数据，1月MLS®系统共成交617套住宅，同比下降24.2%，是最近十年中最不活跃的1月，同比下降13%，比十年平均值低60%。尽管渥太华大范围内的市场活动放缓，买家的议价能力有所增强，卖家活动并不活跃。',
        },
        {
          subtitle: '库存增加：卖方市场转变买方市场',
          text: '从库存数据来看，1月份的待售房源为1,350套，同比增长0.3%，库存量更是达到了3,312套，同比增长65.7%。存量的上升让买家有更多的选择权在市场中议价，市场从卖方市场转向买方市场，市场成交价/开价比例）为54.1%，远低于去年年初的93.3%，反映出卖家的议价能力较弱，买家需要更多买方的议价来吸引市场。',
        },
        {
          subtitle: '房价趋势：市场仍具韧性',
          text: '尽管市场成交量较低，渥太华的房价依然保持稳定。根据OREB，1月MLS®系统中（住宅类物业及公寓物业）的平均价格为$649,900，同比上升5.2%。',
        },
        {
          subtitle: '平均销售价格也呈现上升',
          text: '独立住宅平均价格为$670,258，同比上升5.8%。',
        },
      ],
    },
    2: {
      title: '渥太华首次购房者必知的5个技巧',
      content: [
        {
          subtitle: '技巧 1：了解市场趋势',
          text: '渥太华的房地产市场在2025年有许多变化，首次购房者需要关注最新的市场趋势，例如房价波动和库存变化。',
        },
        {
          subtitle: '技巧 2：选择合适的区域',
          text: '渥太华的不同区域有不同的特点，购房者需要根据自己的需求选择合适的区域，例如靠近市中心或郊区。',
        },
      ],
    },
    3: {
      title: '为什么渥太华是房地产投资的最佳选择',
      content: [
        {
          subtitle: '稳定的经济',
          text: '渥太华作为加拿大的首都，拥有稳定的经济和就业市场，是房地产投资的理想选择。',
        },
        {
          subtitle: '高质量生活',
          text: '渥太华以其高质量的生活和优美的自然环境吸引了大量投资者和居民。',
        },
      ],
    },
    // 可以继续添加其他博客内容
  };

  const blog = blogData[id] || blogData[1];

  return (
    <section className="container-full blog-detail-page">
      <div className="blog-detail-header">
        <h1>{blog.title}</h1>
      </div>
      <div className="blog-detail-content">
        {blog.content.map((section, index) => (
          <div className="blog-detail-section" key={index}>
            <h2>{section.subtitle}</h2>
            <p>{section.text}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default BlogDetail;
