import React, { useEffect } from 'react';

function Services() {
  useEffect(() => {
    // Fade-in animation for the services section
    const servicesSection = document.querySelector('.services');
    servicesSection.style.opacity = '0';
    setTimeout(() => {
      servicesSection.style.transition = 'opacity 1s ease-in';
      servicesSection.style.opacity = '1';
    }, 100);
  }, []);

  return (
    <section className="services">
      {/* 第一屏：背景图片，左边文本右边图片 */}
      <div className="services-header">
        <h2 className="animate-slide-up">SERVICES</h2>
        <div className="service-content service-content-first">
          <div className="service-text">
            <h3 className="animate-slide-up">GET HELP WITH Selling</h3>
            <p className="animate-slide-up">
              Our combination of skill, experience, and technology ensures that we can sell your home for the highest possible price!
            </p>
            <div className="service-buttons animate-slide-up">
              <button className="btn">WHY CHOOSE ME</button>
              <button className="btn">SELLING PROCESS</button>
              <button className="btn">FREE HOME EVALUATION</button>
            </div>
            <div className="service-image">
            <img
              src="/images/services-room.jpg"
              alt="Selling Service"
              className="animate-fade-in"
            />
          </div>
          </div>

        </div>
      </div>

      {/* 第二屏：白色背景，左边图片右边文本 */}
      <div className="services-second">
        <div className="service-content service-content-second">
          <div className="service-image">
            <img
              src="/images/service-room.jpg" // 替换为适合第二屏的图片
              alt="Buying Service"
              className="animate-fade-in"
            />
          </div>
          <div className="service-text">
            <h3 className="animate-slide-up">GET HELP WITH Buying</h3>
            <p className="animate-slide-up">
              We provide expert guidance to help you find your dream home with ease and confidence.
            </p>
            <div className="service-buttons animate-slide-up">
              <button className="btn">WHY CHOOSE ME</button>
              <button className="btn">BUYING PROCESS</button>
              <button className="btn">CONTACT US</button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Services;
