// src/components/Blog.js
import React from 'react';
import { Link } from 'react-router-dom';
import './blog.css';

function Blog() {
  const blogPosts = [
    {
      id: 1,
      title: 'The Future of Real Estate in 2025',
      excerpt: 'Explore the trends shaping the real estate market in 2025, from smart homes to sustainable living.',
      image: 'https://via.placeholder.com/400x300?text=Blog+1',
      date: 'March 15, 2025',
    },
    {
      id: 2,
      title: 'Top 5 Tips for First-Time Home Buyers',
      excerpt: 'Buying your first home? Here are five essential tips to make the process smoother.',
      image: 'https://via.placeholder.com/400x300?text=Blog+2',
      date: 'March 10, 2025',
    },
    {
      id: 3,
      title: 'Why Ottawa is the Best Place to Invest in Real Estate',
      excerpt: 'Discover why Ottawa is becoming a hotspot for real estate investment.',
      image: 'https://via.placeholder.com/400x300?text=Blog+3',
      date: 'March 5, 2025',
    },
    {
      id: 4,
      title: 'How to Stage Your Home for a Quick Sale',
      excerpt: 'Learn the best staging techniques to make your home irresistible to buyers.',
      image: 'https://via.placeholder.com/400x300?text=Blog+4',
      date: 'February 28, 2025',
    },
    {
      id: 5,
      title: 'The Impact of Technology on Real Estate',
      excerpt: 'From virtual tours to AI-driven analytics, technology is transforming real estate.',
      image: 'https://via.placeholder.com/400x300?text=Blog+5',
      date: 'February 20, 2025',
    },
    {
      id: 6,
      title: 'Sustainable Living: Eco-Friendly Homes',
      excerpt: 'How to make your home more sustainable without breaking the bank.',
      image: 'https://via.placeholder.com/400x300?text=Blog+6',
      date: 'February 15, 2025',
    },
    {
      id: 7,
      title: 'The Benefits of Working with a Real Estate Agent',
      excerpt: 'Why hiring a professional can save you time, money, and stress.',
      image: 'https://via.placeholder.com/400x300?text=Blog+7',
      date: 'February 10, 2025',
    },
    {
      id: 8,
      title: 'Renovation Ideas to Increase Your Home’s Value',
      excerpt: 'Simple renovations that can significantly boost your property’s market value.',
      image: 'https://via.placeholder.com/400x300?text=Blog+8',
      date: 'February 5, 2025',
    },
    {
      id: 9,
      title: 'Understanding the Mortgage Process',
      excerpt: 'A step-by-step guide to securing a mortgage for your dream home.',
      image: 'https://via.placeholder.com/400x300?text=Blog+9',
      date: 'January 30, 2025',
    },
    {
      id: 10,
      title: 'The Rise of Remote Work and Its Impact on Housing',
      excerpt: 'How remote work is changing where and how people choose to live.',
      image: 'https://via.placeholder.com/400x300?text=Blog+10',
      date: 'January 25, 2025',
    },
  ];

  return (
    <section className="container-full blog-page">
      <div className="blog-header">
        <h1>Our Blog</h1>
        <p>Stay updated with the latest trends, tips, and insights in real estate.</p>
      </div>
      <div className="blog-grid">
        {blogPosts.map((post) => (
          <div className="blog-card" key={post.id}>
            <div className="blog-card-image">
              <img src={post.image} alt={post.title} />
              <div className="blog-card-overlay">
                <Link to={`/blog/${post.id}`} className="read-more-btn">
                  Read More
                </Link>
              </div>
            </div>
            <div className="blog-card-content">
              <h3>{post.title}</h3>
              <p className="blog-date">{post.date}</p>
              <p className="blog-excerpt">{post.excerpt}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Blog;
