import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';

// 语言内容映射
const languageContent = {
  EN: {
    home: 'Home',
    listings: 'Listings',
    selling: 'Selling',
    buying: 'Buying',
    about: 'About',
    resources: 'Resources',
    Map_search:'Map Search',
    Featured_listings:'Featured Listings',
    Search_listings:'Search Listings',
    Mortgage_calculation: 'Mortgage calculation',
    Loan_calculation: 'Loan calculation',
    Transfer_calculation: 'Transfer calculation',
  },
  Chinese: {
    home: '首页',
    listings: '房源',
    selling: '出售',
    buying: '购买',
    about: '关于',
    resources: '资源',
    Map_search:'地图搜索',
    Featured_listings:'精品房源',
    Search_listings:'搜索房源',
    Mortgage_calculation: '按揭计算',
    Loan_calculation: '贷款计算',
    Transfer_calculation: '转让计算',
  },
  Japanese: {
    home: 'ホーム',
    listings: '物件',
    selling: '売却',
    buying: '購入',
    about: '会社概要',
    resources: 'リソース',
    Map_search:'Mapサーチです',
    Featured_listings:'精品房源',
    Search_listings:'搜索房源',
    Mortgage_calculation: '住宅ローン計算',
    Loan_calculation: 'ローン計算',
    Transfer_calculation: '譲渡計算',
  },
};

function Header() {
  const [isListingsOpen, setIsListingsOpen] = useState(false);
  const [isSellingOpen, setIsSellingOpen] = useState(false);
  const [isBuyingOpen, setIsBuyingOpen] = useState(false); // 为 buying 添加独立状态
  const [isResourcesOpen, setIsResourcesOpen] = useState(false); // 为 resources 添加独立状态
  const [isNavOpen, setIsNavOpen] = useState(false);
  const [language, setLanguage] = useState('EN');
  const [isLanguageOpen, setIsLanguageOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleNav = () => {
    setIsNavOpen(!isNavOpen);
  };

  const toggleLanguageDropdown = (e) => {
    e.preventDefault();
    console.log('Toggling language dropdown, current isLanguageOpen:', isLanguageOpen);
    setIsLanguageOpen((prev) => {
      const newValue = !prev;
      console.log('New isLanguageOpen:', newValue);
      return newValue;
    });
  };

  const selectLanguage = (lang) => {
    console.log('Selecting language:', lang);
    setLanguage(lang);
    console.log('Updated content:', languageContent[lang]);
    setIsLanguageOpen(false);
  };

  const content = languageContent[language];
  console.log('Current content:', content); // 调试日志

  return (
    <header>
      <div className="container header-content">
        <div className="logo-section">
          <div className="logo">
            <img src="/images/logo.jpg" alt="Joanne Realty Logo" />
          </div>
          <span className="broker-name">Joanne Wang Broker</span>
        </div>

        {isMobile && (
          <button className="hamburger" onClick={toggleNav}>
            <FontAwesomeIcon icon={faBars} />
          </button>
        )}

        <nav className={isNavOpen || !isMobile ? 'nav-open' : ''}>
          <ul>
            <li>
              <a href="#home">{content.home}</a>
            </li>
            <li
              onMouseEnter={() => setIsListingsOpen(true)}
              onMouseLeave={() => setIsListingsOpen(false)}
            >
              <a href="#listings">
                {content.listings} <span className="dropdown-icon">▼</span>
              </a>
              <div
                className="dropdown listings-dropdown"
                style={{ display: isListingsOpen ? 'block' : 'none' }}
              >
                <a href="#map-search">Map Search</a>
                <a href="#criteria-search">Featured Listings</a>
                <a href="#community-search">Search Listings</a>
              </div>
            </li>
            <li
              onMouseEnter={() => setIsSellingOpen(true)}
              onMouseLeave={() => setIsSellingOpen(false)}
            >
              <a href="#selling">
                {content.selling} <span className="dropdown-icon">▼</span>
              </a>
              <div
                className="dropdown"
                style={{ display: isSellingOpen ? 'block' : 'none' }}
              >
                <a href="#Selling Resources">Selling Resources</a>
                <a href="#Free Evaluation">Free Evaluation</a>
              </div>
            </li>
            <li
              onMouseEnter={() => setIsBuyingOpen(true)}
              onMouseLeave={() => setIsBuyingOpen(false)}
            >
              <a href="#buying">
                {content.buying} <span className="dropdown-icon">▼</span>
              </a>
              <div
                className="dropdown"
                style={{ display: isBuyingOpen ? 'block' : 'none' }}
              >
                <a href="#why-choose-me">Buying Resources</a>
              </div>
            </li>
            <li>
              <a href="#about">{content.about}</a>
            </li>
            <li
              onMouseEnter={() => setIsResourcesOpen(true)}
              onMouseLeave={() => setIsResourcesOpen(false)}
            >
              <a href="#resources">
                {content.resources} <span className="dropdown-icon">▼</span>
              </a>
              <div
                className="dropdown"
                style={{ display: isResourcesOpen ? 'block' : 'none' }}
              >
                <a href="https://www.realtor.ca/calculator#v=payment" target="blank">
                  {content.Mortgage_calculation}
                </a>
                <a href="https://www.realtor.ca/calculator#v=affordability" target="blank">
                  {content.Loan_calculation}
                </a>
                <a href="https://www.realtor.ca/calculator#v=landtransfertax" target="blank">
                  {content.Transfer_calculation}
                </a>
              </div>
            </li>

            {isMobile && (
              <li className="mobile-language">
                <a href="#language" onClick={toggleLanguageDropdown}>
                  {language}
                </a>
                <div
                  className="dropdown language-dropdown"
                  style={{ display: isLanguageOpen ? 'block' : 'none' }}
                >
                  <a href="#en" onClick={() => selectLanguage('EN')}>
                    EN
                  </a>
                  <a href="#zh" onClick={() => selectLanguage('Chinese')}>
                    Chinese
                  </a>
                  <a href="#jp" onClick={() => selectLanguage('Japanese')}>
                    Japanese
                  </a>
                </div>
              </li>
            )}
          </ul>
        </nav>


        {!isMobile && (
          <div className="language-toggle">
            <a href="#language" onClick={toggleLanguageDropdown}>
              {language}
            </a>
            <div
              className="dropdown language-dropdown"
              style={{ display: isLanguageOpen ? 'block' : 'none' }}
            >
              <a href="#en" onClick={() => selectLanguage('EN')}>
                EN
              </a>
              <a href="#zh" onClick={() => selectLanguage('Chinese')}>
                Chinese
              </a>
              <a href="#jp" onClick={() => selectLanguage('Japanese')}>
                Japanese
              </a>
            </div>
          </div>
        )}
      </div>
    </header>
  );
}

export default Header;
